		self.bName = name
		self.bPrice = price
		self.bIsbn = isbn

class BookRepository:
	def __init__(self):
		self.bDic = &#123;&#125;

	def registBook(self,b):
		self.bDic[b.bIsbn] = b

	def removebook(self, isbn):
		del self.bDic[bIsbn]

	def printBooksInfo(self):
		for isbn in self.bDic.keys():
			b = self.bDic[isbn]
			print(f'&#123;b.bName&#125;,&#123;b.bPrice&#125;,&#123;b,bIsbn&#125;')

	def printBookInfo(self, isbn):
