from abc import ABCMeta
from abc import abstractmethod

class AbsDictionary(metaclass=ABCMeta):
	def __init__(self):
		self.wordDic = &#123;&#125;
	@abstractmethod
	def registWord(Self,w1,w2):
		pass
	@abstractmethod
	def removeWord(self,w1):
		pass
	@abstractmethod
	def updateWord(self,w1,w2):
		pass
	@abstractmethod
	def searchWord(self,w1):
		pass

class Kor2Eng(AbsDictionary):
	def __init__(self):
		super().__init__()

	def registWord(self,w1,w2):
		print(f'[Kor2Eng]registWord(): &#123;w1&#125; to &#123;w2&#125;')
		self.wordDic[w1] = w2 #superior to Kor2Eng() 
	def removeWord(self,w1):
		print(f'[Kor2Eng] removeWord(): &#123;w1&#125;')
		del self.wordDic[w1]

	def updateWord(self,w1,w2):
		print(f'[Kor2Eng] updateWord(): &#123;w1&#125; to &#123;w2&#125;')
		self.wordDic[w1] = w2

	def searchWord(self,w1):
		print(f'[Kor2Eng] searchWord() : &#123;w1&#125;')
		return self.wordDic[w1]

	def printWords(self):
		for k in self.wordDic.keys():
			print(f'&#123;k&#125;: &#123;self.wordDic[k]&#125;')

class Kor2Jpn(AbsDictionary):
