	def __init__(self,n):
		super().__init__(f'&#123;n&#125; is a prime number.')

def isPrime(number):
	flag = True
	for n in range(2,number):
		if number % n == 0:
			flag == False
			break
	if flag == False:
		raise NotPrimeExcpetion(number)
	else: 
		raise PrimeException(number)

